INSERT INTO `bookall` VALUES ('2358', '醒来的女性', '未读', '90.00元', '2017-7', ' 北京联合出版公司', '../resources/bookimg/38.png', '我们都是受困于时代的“囚鸟”——既渴望逃离，又踟蹰不前。\n联邦最低限度安保措施成人改造所里，颓唐的小老头儿瓦尔特•斯代布克正在等待领他出狱的狱卒。\n在他过去的人生中，他曾是斯拉夫移民的儿子，哈佛大学毕业生，前共产党党员，前联邦政府官员，“水门事件”的涉案者……\n不久他还将获得一个新的身份，神秘的……', '9787550296787', '143');
