INSERT INTO `bookall` VALUES ('2626', '了不起的盖茨比', '[美]弗·司各特·菲茨杰拉德', '12.00元', '2004-06', ' 人民文学出版社', '../resources/bookimg/306.png', '盖茨比为了久久地抱着的一个梦而付出了很高的代价。他死后，尼克发觉是汤姆暗中挑拨威尔逊去杀死盖茨比。他感到东部鬼影幢幢，世态炎凉，便决定回中西部老家去。这是一个简单的故事，却有着极悲凉的人生况味。', '9787020046089', '157');
