INSERT INTO `bookall` VALUES ('2851', '瓦尔登湖', '亨利·戴维·梭罗', '11.00元', '2006-8', ' 上海译文出版社', '../resources/bookimg/147.png', '这本书的思想是崇尚简朴生活，热爱大自然的风光，内容丰厚，意义深远，语言生动，意境深邃，就像是个智慧的老人，闪现哲理灵光，又有高山流水那样的境界。\n书中记录了作者隐居瓦尔登湖畔，与大自然水乳交融、在田园生活中感知自然重塑自我的奇异历程。读本书，能引领人进入一个澄明、恬美、素雅的世界。', '9787532739578', '149');
