INSERT INTO `bookall` VALUES ('2861', '雨季不再来', '三毛', '28.00元', '2007-7', ' 北京十月文艺出版社', '../resources/bookimg/157.png', '《雨季不再来》以三毛的生命历程为主题，记录了三毛17岁到22岁的成长过程，真实呈现出三毛少女时代的成长感受，辍学、自闭、叛逆，游学西班牙、德国、美国后，渐渐成长为独立自信的青年，这本书中透露的纯真情怀和异质美感，可以清楚地印证她传奇性格的痕迹。', '9787530208960', '149');
