INSERT INTO `bookall` VALUES ('2892', '万物静默如谜', '[波兰]维斯瓦娃·辛波斯卡', '26.00元', '2012-8', ' 浦睿文化·湖南文艺出版社', '../resources/bookimg/188.png', '《辛波斯卡诗选：万物静默如谜》收录辛波斯卡各阶段名作75首，包括激发知名绘本作家幾米创作出《向左走，向右走》的《一见钟情》，收录高中语文教材的《底片》，网上广为流传的《在一颗小星星下》《种种可能》等，曾获得《洛杉矶时报》年度最佳图书。\n辛波斯卡的诗取材于日常生活的事物和经验，甲虫、海参、石头、沙粒、天空；安眠药、履历表、衣服；电影、画作、剧场、梦境等等，在她的笔下无不焕发出新的诗意，让人们重新认识生活中常见的事物。', '9787540457228', '151');
