INSERT INTO `bookall` VALUES ('2899', '人间词话', '王国维', '9.80元', '1998-12-01', ' 上海古籍出版社', '../resources/bookimg/195.png', '《人间词话》一书乃是王氏接受了西洋美学思想之洗礼后，以崭新的眼光对中国旧文学所作的评论，具有划时代的意义，向来极受学术界重视。本书并约请当今著名专家黄霖为之导读，不仅梳理其理论框架，剔抉其精义要眇，更着重揭橥其学术源流、历史文化背景，及撰作者当时特定的情境与心态，从而在帮助读者确切理解原著的同时，凸现词学大师王国维的学术个性。', '9787532524808', '151');
