INSERT INTO `bookall` VALUES ('2902', '荒原', '艾略特文集', '45.00', '2012-7-1', ' 上海译文出版社', '../resources/bookimg/198.png', '艾略特被称为“世界诗歌漫长历史中一个新阶段的带领人”，是英语诗歌界“最有影响的诗人之一”。本书全面收录了T·S·艾略特的诗歌。他在诗歌中通过用典暗示、启发读者，含蓄而且深刻地表达对当代西方社会的看法和自己的心路历程。', '9787532755059', '151');
