INSERT INTO `bookall` VALUES ('2905', '牧羊少年奇幻之旅', '[巴西]保罗·柯艾略', '25.00元', '2009-3-1', ' 南海出版公司', '../resources/bookimg/201.png', '牧羊少年圣地亚哥接连两次做了同一个梦，梦见埃及金字塔附近藏有一批宝藏。少年卖掉羊群，历尽千辛万苦一路向南，跨海来到非洲，穿越“死亡之海”撒哈拉大沙漠……期间奇遇不断，在一位炼金术士的指引下，他终于到达金字塔前，悟出了宝藏的真正所在……', '9787544244190', '152');
