INSERT INTO `bookall` VALUES ('2950', '人间词话', '王国维', '9.80元', '1998-12-01', ' 上海古籍出版社', '../resources/bookimg/248.png', '《人间词话》一书乃是王氏接受了西洋美学思想之洗礼后，以崭新的眼光对中国旧文学所作的评论，具有划时代的意义，向来极受学术界重视。本书并约请当今著名专家黄霖为之导读，不仅梳理其理论框架，剔抉其精义要眇，更着重揭橥其学术源流、历史文化背景，及撰作者当时特定的情境与心态，从而在帮助读者确切理解原著的同时，凸现词学大师王国维的学术个性。', '9787532524808', '154');
INSERT INTO `bookall` VALUES ('2951', '红楼梦', '果麦文化', '88.00元', '2016-11-1', ' 三秦出版社', '../resources/bookimg/249.png', '', '9787551813570', '154');
INSERT INTO `bookall` VALUES ('2952', '鬼谷子', '中华经典名著全本全注全译丛书', '15.00元', '2011-12', ' 中华书局', '../resources/bookimg/250.png', '《鬼谷子》是人类文明 “轴心时代”产生的一部奇书，其思想内容十分丰富，涵盖了哲学、政治学、军事学、心理学、社会学、文学、情报学等多种学科，是一部可以被广泛解读的著作。《鬼谷子》提出了“捭阖”、“反应”、“内揵”、“飞箝”、“忤合”、“揣摩”等游说和谋略的原则和技巧，对纵横家学说的理论构建做出了重要贡献。它提供的智慧与谋略至今仍对现实生活中的方方面面有着指导意义。', '9787101083149', '154');
