INSERT INTO `bookall` VALUES ('2959', '东京梦华录', '国学经典丛书', '20.00元', '2010-6-1', ' 中州古籍出版社', '../resources/bookimg/257.png', '《东京梦华录》多记崇宁以后所见，时方以逸豫临下，故若彩山灯火，水殿争标，宝津男女诸戏，走马角射，及天宁节女队归骑，年少争迎，虽事隔前载，犹令人想见其盛。至如都人探春，游娱池苑，京瓦奏技，茶酒坊肆，晓贩夜市，交易琐细，率皆依准方俗，尢强藻润，自能详不尽杂，质不坠俚，可谓善记风土者。', '9787534833533', '154');
