INSERT INTO `musicall` VALUES ('1044', 'Cassa Nova', '落日飛車SunsetRollercoaster', '专辑', 'CD', '2018-03-14', '自主練習工作室', '../resources/musicimg/169.png', '　　卡薩諾瓦，又名半熟王子，是落日飛車繼金桔希子後的全新錄音室作品。 透過九首歌曲來述說故事的主人翁卡薩諾瓦的熟成冒險旅程。<br/>　　卡薩諾瓦的自我意識在追尋愛情的過程中萌芽，有如一朵美麗的玫瑰，在東方遙遠寧靜的夜裡蘇醒，溫泉里的玉子孵化成了半熟的王子。<br/>　　這張作品企圖心旺盛，<br/>　　詞曲除了延續以往的英文創作更挑戰了法文與拉丁文；<br/>　　而在編曲上則大膽的挑戰數位調變技術，<br/>　　搭配飛車既往復古懷舊的音樂氛圍，期待能為亞洲新迷幻注入當代視野。', '141');
INSERT INTO `musicall` VALUES ('1045', 'True', 'Avicii', '专辑', 'Audio CD', '2013-09-17', 'Island', '../resources/musicimg/170.png', '　　卡薩諾瓦，又名半熟王子，是落日飛車繼金桔希子後的全新錄音室作品。 透過九首歌曲來述說故事的主人翁卡薩諾瓦的熟成冒險旅程。<br/>　　卡薩諾瓦的自我意識在追尋愛情的過程中萌芽，有如一朵美麗的玫瑰，在東方遙遠寧靜的夜裡蘇醒，溫泉里的玉子孵化成了半熟的王子。<br/>　　這張作品企圖心旺盛，<br/>　　詞曲除了延續以往的英文創作更挑戰了法文與拉丁文；<br/>　　而在編曲上則大膽的挑戰數位調變技術，<br/>　　搭配飛車既往復古懷舊的音樂氛圍，期待能為亞洲新迷幻注入當代視野。', '141');
