INSERT INTO `musicall` VALUES ('1353', 'Not Going Anywhere', 'KerenAnn', 'Import', 'Audio CD', '2004-08-24', 'Capitol/Blue Note', '../resources/musicimg/484.png', '　　KerenAnn何许人也？她是法国乐坛最受瞩目的民谣才女，她出生在以色列，从小学吉他时就开始扒Joni Mitchell的曲子，11岁举家迁往巴黎后竟然得到了JoniMitchell的赏识而结识了著名制作人Benjamin Biolay，后者的古典音乐背景为她的作品带来更丰富的编配层次。2000年KerenAnn的首张专辑《LaBiographie De Luka Philipsen》融入了俄国文学、犹太民谣和法国诗歌，并以Trip-Hop、民谣及法国流行乐的融合形式现身，一举在欧洲赢得巨大成功。一曲《Jardind’Hiver》更是惊动了80高龄的法国Bossa Nova鼻祖HenriSalvador，后者在自己的经典复出专辑《Chambre Avec Vue》中收录了包括《Jardind’Hiver》在内的5首由BenjaminBiolay和...', '157');
INSERT INTO `musicall` VALUES ('1354', 'On S\'Aime', 'GérardDarmon', '专辑', 'Audio CD', '2008-12-23', 'Universal France', '../resources/musicimg/485.png', '　　KerenAnn何许人也？她是法国乐坛最受瞩目的民谣才女，她出生在以色列，从小学吉他时就开始扒Joni Mitchell的曲子，11岁举家迁往巴黎后竟然得到了JoniMitchell的赏识而结识了著名制作人Benjamin Biolay，后者的古典音乐背景为她的作品带来更丰富的编配层次。2000年KerenAnn的首张专辑《LaBiographie De Luka Philipsen》融入了俄国文学、犹太民谣和法国诗歌，并以Trip-Hop、民谣及法国流行乐的融合形式现身，一举在欧洲赢得巨大成功。一曲《Jardind’Hiver》更是惊动了80高龄的法国Bossa Nova鼻祖HenriSalvador，后者在自己的经典复出专辑《Chambre Avec Vue》中收录了包括《Jardind’Hiver》在内的5首由BenjaminBiolay和...', '157');
