INSERT INTO `musicall` VALUES ('1449', 'Epitaph', 'GodIsAnAstronaut', 'Import', 'Audio CD', '2018-04-27', 'Napalm', '../resources/musicimg/583.png', '　　Ever since forming 16 years ago, Irish trio God Is An Astronaut has reigned as one of the premier instrumental groups. With its fusion of intense rock instrumentation, ethereal and emotional soundscapes, awe-inspiring dynamics and transfixing melodies.<br/>　　Each of their prior releases has offered a breath taking sonic journey, and their ninth emotive opus, Epitaph, is a rec...', '162');
