INSERT INTO `musicall` VALUES ('1577', '最好的快要发生', '张栋梁', 'EP', 'CD', '2018-03-21', '红豆娱乐', '../resources/musicimg/730.png', '　　五度世界裡 微慢暖的真心不变<br/>　　新时代的暖灵魂 张栋梁<br/>　　2018全新个人音乐EP『最好的快要发生』<br/>　　突破自我 全新融合<br/>　　5首全新蜕变曲目<br/>　　＊晴天树 ＊最好的快要发生<br/>　　＊自己的风雨 ＊玻璃缸 ＊鱼与熊掌', '169');
INSERT INTO `musicall` VALUES ('1578', '浴室', 'Decajoins', '专辑', 'CD', '2017-06-08', 'Airhead Records', '../resources/musicimg/731.png', '　　今年春天最适合听的乐团<br/>　　「一池春水 如梦的台北 多美好的人生 在颓败的地方找到快乐」<br/>　　从一个最混沌的状态里发芽，有过暴裂有过激热，很快的就开始等待最后一秒的坠地了。他需要温柔而抽离的酒精，唱漫天舞地的歌；干枯的浴室他留不住，用仅剩的个人倾向跳入蓝色的海，裸身浮沉到海口，静而凝视的姿态、拧气闭眼。<br/>　　「你会不会一去不回来 ？我在烟里等你」<br/>　　＿＿＿无法排遣、无法打发，无法洒脱地丢弃，只能坦坦面对，豁豁地与之相处。去去回回，堵了那扇门有另一散扇窗，用缓慢拖沈的喃喃说一种潮湿的快乐，闷着黏着困着，不留余地得把痛苦也合而为一。', '169');
